require('dotenv').config();
const express = require('express');
const cors = require('cors');
const morgan = require('morgan');
const { validationResult } = require('express-validator');

// Routes
const HomeRoutes = require('./routes/HomeRoutes');
const LibraryRoutes = require('./routes/LibraryRoutes');
const StoryRoutes = require('./routes/StoryRoutes');
const ShopRoutes = require('./routes/ShopRoutes');
const QuizRoutes = require('./routes/QuizRoutes');
const BookmarkRoutes = require('./routes/BookmarkRoutes');
const QuestRoutes = require('./routes/QuestRoutes');

const userRoute = require('./routes/userRoute');
const studentRoute = require('./routes/studentRoute');
const teacherRoute = require('./routes/teacherRoute');
const settingsRoute = require('./routes/settingsRoute');
const streakRoute = require('./routes/streakRoute');
const wordHelperRoute = require('./routes/wordHelperRoute');
const paymentRoute = require('./routes/paymentRoute');
const rankingRoute = require('./routes/rankingRoute');

const app = express();

// ------------------ Middlewares ------------------
app.use(cors());
app.use(morgan('dev'));

// PayMaya webhook must be before JSON parser
app.use('/api/webhook/paymaya', express.raw({ type: 'application/json' }));

app.use(express.json({ limit: '5mb' }));

// Global validation error handler
app.use((req, res, next) => {
  const errors = validationResult(req);
  if (!errors.isEmpty()) {
    return res.status(400).json({
      success: false,
      errors: errors.array().map(err => ({ field: err.path || err.param, message: err.msg })),
    });
  }
  next();
});

// ------------------ Mount Routes ------------------
// CozyClip Features (routes modules already define /api/* paths)
app.use('/', HomeRoutes);
app.use('/', LibraryRoutes);
app.use('/', StoryRoutes);
app.use('/', ShopRoutes);
app.use('/', QuizRoutes);
app.use('/', BookmarkRoutes);
app.use('/', QuestRoutes);

// Legacy / User System
app.use(userRoute);
app.use(studentRoute);
app.use(teacherRoute);
app.use(settingsRoute);
app.use(streakRoute);
app.use(wordHelperRoute);
app.use(paymentRoute);
app.use('/api', rankingRoute);

// Root
app.get('/', (req, res) => {
  res.json({
    success: true,
    message: 'CozyClip Stories API — Running Perfectly',
    version: '1.0',
    features: 'Library • Quiz • Shop • Quests • Firebase • Payments',
  });
});

// Health check
app.get('/health', (req, res) => {
  const hasDb = !!(req.app && req.app.locals && req.app.locals.db);
  res.json({ success: true, uptime: process.uptime(), db: hasDb });
});

// 404 Handler
app.use((req, res) => {
  res.status(404).json({ success: false, message: 'Route not found' });
});

module.exports = app;
